import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import Webcam from 'react-webcam';
import { loadModels, getFullFaceDescription, createMatcher } from '../api/face';
import DrawBox from '../components/drawBox';
import { JSON_PROFILE } from '../common/profile';

const WIDTH = 1050;
const HEIGHT = 750;
const inputSize = 288;

class CameraFaceDetect extends Component {
  constructor(props) {
    super(props);
    this.webcam = React.createRef();
    this.state = {
      fullDesc: [],
      faceMatcher: null,
      facingMode: null
    };
  }

  componentWillMount() {
    loadModels();
    this.setInputDevice();
    this.matcher();
  }

  setInputDevice = () => {
    navigator.mediaDevices.enumerateDevices().then(async devices => {
      let inputDevice = await devices.filter(
        device => device.kind === 'videoinput'
      );

      if (inputDevice.length < 2) {
        await this.setState({
          facingMode: 'user'
        });
      } else {
        await this.setState({
          facingMode: { exact: 'environment' }
        });
      }

      this.startCapture();
    });
  };

  matcher = async () => {
    const faceMatcher = await createMatcher(await JSON_PROFILE());
    this.setState({ faceMatcher });
  };

  // AMPLIO EL INTERVALO DE ESPERA A 2 SEG
  startCapture = () => {
    this.interval = setInterval(() => {
      this.capture();
    }, 2000);
  };

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  capture = async () => {
    if (!!this.webcam.current) {
      await getFullFaceDescription(
        this.webcam.current.getScreenshot(), WIDTH, HEIGHT,
        inputSize
      ).then(fullDesc => this.setState({ fullDesc }));
    }
  };

  render() {
    const { fullDesc, faceMatcher, facingMode } = this.state;
    let videoConstraints = null;
    let camera = '';

    if (!!facingMode) {

      videoConstraints = {
        width: WIDTH,
        height: HEIGHT,
        facingMode: facingMode
      };

      if (facingMode === 'user') {camera = 'Frontal';
      } else {camera = 'Trasera';}

    }

    return (
      <div
        className="Camera"
        style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center'
        }}>

        <p>CÃ¡mara: {camera}, rostros detectados: {this.state.fullDesc.length}</p>
        <div
          style={{
            width: WIDTH,
            height: HEIGHT
          }}
        >
          <div style={{ position: 'relative', width: WIDTH }}>
            {!!videoConstraints ? (
              <div style={{ position: 'absolute' }}>
                <Webcam
                  audio={false}
                  width={WIDTH}
                  height={HEIGHT}
                  ref={this.webcam}
                  screenshotFormat="image/jpeg"
                  videoConstraints={videoConstraints}
                />
              </div>
            ) : null}
            {!!fullDesc ? (
              <DrawBox
                fullDesc={fullDesc}
                faceMatcher={faceMatcher}
                imageWidth={WIDTH}
                boxColor={'blue'}
              />
            ) : null}
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(CameraFaceDetect);
